mapper.select:
SELECT COUNT(*)
FROM TB_USER
WHERE USER_ID = #{userId}

mapper.update:
UPDATE TB_USER
SET LOGIN_ERR_CNT = 0
WHERE USER_ID = #{userId}

UPDATE TB_USER
SET LOGIN_ERR_CNT = LOGIN_ERR_CNT +1 
WHERE USER_ID = #{userId}

UPDATE TB_USER
SET LOGIN_RESTRICTED = #{loginRestricted}
WHERE USER_ID = #{userId}
